select * from referensi.tr_jenis_layanan_nle
WHERE EXISTS (
    SELECT 1
    FROM jsonb_array_elements_text(kode_kbli) AS elem
    WHERE elem IN ('49431', '52108', '52109')
);

SELECT DISTINCT id_jenis_layanan ,j.jenis_layanan
FROM referensi.tr_jenis_layanan_nle j
JOIN LATERAL jsonb_array_elements_text(j.kode_kbli) AS elem(kd) ON elem.kd IN ('49431', '52108', '52109');




